import styled from 'styled-components'

export const ButtonContainer = styled.button `
  text-transform:capitalize;
  font-size: 1.4rem;
  background: transparent;
  border: 2px solid black;
  border-radius: 5px;
  transition: all 0.5s ease-in-out;
  &:hover{
  	 background: darkBlue;
  	 color: white;
  }
`;
