/* GearPro Website
    Version 1.0 Author: Jason Sheppard
    Comissioned: Jody Moore */
$(function() {

var topoffset= 65;

  //Use smooth scrolling when clicking on navigation
  $('.navbar a[href*=\\#]:not([href=\\#])').click(function() {
    if (location.pathname.replace(/^\//,'') ===
      this.pathname.replace(/^\//,'') &&
      location.hostname === this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
      if (target.length) {
        $('html,body').animate({
          scrollTop: target.offset().top-topoffset+2
        }, 500);
        return false;
      } //target.length
    } //click function
  }); //smooth scrolling


  $('#site-modal').on('show.bs.modal', function(event) {
    $(this)
      .find('.modal-content img')
      .attr('src', $(event.relatedTarget).data('highres'));
  });
});



// Render the PayPal button into #paypal-button-container

paypal.Buttons({
createOrder: function(data, actions) {
  return actions.order.create({
    purchase_units: [{
      amount: {
        value: '0.01'
      }
    }]
  });
},
onApprove: function(data, actions) {
  return actions.order.capture().then(function(details) {
    alert('Transaction completed by '+ 'order ID: ' + data.orderID + details.payer.name.given_name);
    // Call your server to save the transaction
    return fetch('/paypal-transaction-complete', {
      method: 'post',
      headers: {
        'content-type': 'application/json'
      },
      body: JSON.stringify({
        orderID: data.orderID

      })

    });

  });
}
}).render('#paypal-button-container');






